mapper.insert:
INSERT INTO CATEGORY (
	CATE_CODE
	, CATE_NAME
) VALUES(
	(SELECT 'CATE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CATE_CODE, 6, 3))), 0) + 1, 3, '0') FROM CATEGORY)
	, #{cateName}
)

mapper.select:
SELECT RNUM, ITEM_CODE, CATE_CODE, ITEM_NAME, TOTAL_PRICE, BUY_CNT, BUY_DATE FROM( SELECT ROWNUM RNUM , ITEM_CODE, CATE_CODE, ITEM_NAME, TOTAL_PRICE, BUY_CNT, BUY_DATE FROM( SELECT ITEM_CODE , ITEM_NAME , CATE_CODE , TOTAL_PRICE , BUY_CNT , TO_CHAR(BUY_DATE, 'YYYY.MM.DD') AS BUY_DATE FROM SALES
WHERE CATE_CODE = #{cateCode}
ORDER BY BUY_DATE ) ) WHERE RNUM BETWEEN #{startRow} and #{endRow}

mapper.insert:
