		, #{imgInfo.attachedImgName}
		, #{imgInfo.itemCode}
		, #{imgInfo.isMain}
	FROM DUAL

INSERT INTO ITEM(
	ITEM_CODE
	, ITEM_PRICE
	, ITEM_INTRO
	, ITEM_STOCK
	, ITEM_STATUS
	, ITEM_NAME
	, CATE_CODE
) VALUES(
	#{itemCode}
	, #{itemPrice}
	, #{itemIntro}
	, #{itemStock}
	, #{itemStatus}
	, #{itemName}
	, #{cateCode}
)

mapper.select:
SELECT RNUM , BUY_CODE, ITEM_NAME, TOTAL_PRICE, BUY_CNT, BUY_DATE, ID, PAYMENT_PLAN, BUY_STATUS, ROAD_ADDR, DETAIL_ADDR FROM ( SELECT ROWNUM RNUM , BUY_CODE, ITEM_NAME, TOTAL_PRICE, BUY_CNT, BUY_DATE, ID, PAYMENT_PLAN, BUY_STATUS, ROAD_ADDR, DETAIL_ADDR FROM ( SELECT BUY_CODE , ITEM_NAME , TOTAL_PRICE , BUY_CNT , TO_CHAR(BUY_DATE, 'YYYY-MM-DD') AS BUY_DATE , ID , PAYMENT_PLAN , BUY_STATUS , ROAD_ADDR , DETAIL_ADDR FROM ORDER_INFO WHERE 1 = 1
AND TO_CHAR(BUY_DATE, 'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
AND BUY_STATUS = #{serchStatus}
ORDER BY BUY_DATE DESC ) ) WHERE RNUM BETWEEN #{startRow} and #{endRow}

SELECT COUNT(BUY_CODE) FROM ORDER_INFO
WHERE 1 = 1

AND TO_CHAR(BUY_DATE, 'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}  


AND BUY_STATUS = #{serchStatus}

mapper.update:
UPDATE BUY_INFO
SET BUY_STATUS = #{statusName}
WHERE BUY_CODE = #{buyCode}

mapper.select:
SELECT COUNT(ITEM_CODE)
FROM SALES
WHERE CATE_CODE = #{cateCode}

mapper.update:
UPDATE ITEM
SET ITEM_STATUS = #{itemStatus}
	, ITEM_STOCK = #{itemStock}
WHERE ITEM_CODE = #{itemCode}

mapper.select:
SELECT SIDE_MENU_CODE, SIDE_MENU_NAME, SM.MENU_PASS, SM.MENU_CODE FROM MENU M , SIDE_MENU SM WHERE M.MENU_CODE = SM.MENU_CODE
AND SM.MENU_CODE = #{menuCode}
AND M.MENU_CODE = 'MENU_001'

SELECT NVL(SUM(TOTAL_PRICE), 0)
FROM ORDER_INFO

WHERE TO_CHAR(BUY_DATE, 'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}

SELECT ITEM_STOCK
FROM ITEM
WHERE ITEM_CODE = #{itemCode}
