mapper.insert:
		INSERT INTO ITEM_CART(
			CART_CODE
			, ITEM_CODE
			, ITEM_CNT
			, ID
		) VALUES (
			(SELECT 'CART_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CART_CODE, 6, 4))), 0) + 1, 4, '0')
         	 FROM ITEM_CART)
			, #{itemCode}
			, #{itemCnt}
			, #{id}
		)

mapper.delete:
DELETE ITEM_CART
WHERE ID = #{id}
AND ITEM_CODE = #{itemCode}

mapper.update:
UPDATE ITEM_CART
SET ITEM_CNT = (SELECT ITEM_CNT FROM ITEM_CART WHERE ID = #{id} AND ITEM_CODE = #{itemCode}) + #{itemCnt}
WHERE ID = #{id}
AND ITEM_CODE = #{itemCode}

mapper.delete:
DELETE ITEM_CART
WHERE ITEM_CODE = #{itemCode}
AND ID = #{id}

mapper.select:
SELECT COUNT(ITEM_CODE)
FROM ITEM_CART
WHERE ID = #{id}
AND ITEM_CODE = #{itemCode}

mapper.insert:
INSERT INTO BUY_INFO(
	BUY_CODE
	, ITEM_CODE
	, ID
	, TOTAL_PRICE
	, BUY_CNT
	, DELIVERY_NAME
	, DELIVERY_TELL
	, DELIVERY_EMAIL
	, DELIVERY_MSG
	, POST_CODE
	, ROAD_ADDR
	, DETAIL_ADDR
	, ORDER_CODE
	, ATTACHED_IMG_NAME
) VALUES (
	(SELECT 'BUY_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BUY_CODE, 6, 3))), 0) + 1, 4, '0') FROM BUY_INFO)
	, #{itemCode}
	, #{id}
	, #{totalPrice}
	, #{buyCnt}
	, #{deliveryName}
	, #{deliveryTell}
	, #{deliveryEmail}
	, #{deliveryMsg}
	, #{postCode}
	, #{roadAddr}
	, #{detailAddr}
	, #{orderCode}
	, #{attachedImgName}
)

mapper.select:
SELECT BUY_DATE
FROM BUY_INFO
WHERE ORDER_CODE = #{orderCode}

SELECT ORDER_CODE
FROM BUY_INFO
WHERE ID = #{id}

SELECT TO_CHAR(BUY_DATE, 'YYYY-MM-DD') AS BUY_DATE , ORDER_CODE , ATTACHED_IMG_NAME , ITEM_CODE , BUY_CNT , TOTAL_PRICE , BUY_STATUS , (SELECT ITEM_NAME FROM ITEM I WHERE BI.ITEM_CODE = I.ITEM_CODE) AS ITEM_NAME , (SELECT CATE_CODE FROM ITEM I WHERE BI.ITEM_CODE = I.ITEM_CODE) AS CATE_CODE FROM BUY_INFO BI WHERE ID = #{id}
AND TO_CHAR(BUY_DATE, 'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
ORDER BY ORDER_CODE DESC

SELECT ITEM_CODE 
FROM BUY_INFO 
WHERE ID = #{id}
AND ITEM_CODE = #{itemCode}
AND ROWNUM = 1

SELECT ITEM_CODE
FROM CART_VIEW
WHERE ID = #{id}
