mapper.insert:
INSERT INTO FEEDBACK(
	FB_CODE
	,TITLE
	,CONTENT
	,ORIGINAL_FILE_NAME
	,ATTACHED_FILE_NAME
	,ITEM_CODE
	,ID
)
VALUES(
	(SELECT 'FB_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(FB_CODE,4,3))),0)+1,3,0) FROM FEEDBACK)
	,#{title}
	,#{content}
	,#{originalFileName}
	,#{attachedFileName}
	,#{itemCode}
	,#{id}
)

mapper.update:
UPDATE FEEDBACK
SET	TITLE = #{title}
	, CONTENT = #{content}
	
	, ATTACHED_FILE_NAME = #{attachedFileName}
	, ORIGINAL_FILE_NAME = #{originalFileName}
	
	, CREATE_DATE = SYSDATE
WHERE FB_CODE = #{fbCode}
