mapper.select:
SELECT ITEM.ITEM_CODE , ITEM_NAME , ITEM_PRICE , ITEM_INTRO , ATTACHED_IMG_NAME , CATE_CODE , ROWNUM FROM ITEM ITEM, ITEM_IMG IMG WHERE ITEM.ITEM_CODE = IMG.ITEM_CODE
AND CATE_CODE = #{cateCode}
AND IS_MAIN = 'Y' ORDER BY ITEM_CODE DESC

SELECT ITEM.ITEM_CODE , ITEM_NAME , ITEM_PRICE , ITEM_INTRO , ATTACHED_IMG_NAME , CATE_CODE , ROWNUM FROM ITEM ITEM, ITEM_IMG IMG WHERE ITEM.ITEM_CODE = IMG.ITEM_CODE
AND CATE_CODE = #{cateCode}
AND IS_MAIN = 'Y' AND ITEM_STATUS = 2 AND ITEM_STOCK < 50

SELECT ITEM.ITEM_CODE
	, ITEM_NAME
	, ITEM_INTRO
	, ITEM_PRICE
	, CATE_CODE
	, ATTACHED_IMG_NAME
	, ROWNUM
FROM ITEM ITEM, ITEM_IMG IMG
WHERE ITEM.ITEM_CODE = IMG.ITEM_CODE
AND IS_MAIN = 'Y'

AND ITEM_NAME LIKE '%'||#{searchValue}||'%'


AND CATE_CODE = #{searchKeyword}


AND ITEM_PRICE >= #{minPrice}
AND ITEM_PRICE <= #{maxPrice}
AND ITEM_PRICE <= #{maxPrice}


AND ITEM_PRICE >= #{minPrice}


AND ITEM_PRICE <= #{maxPrice}


ORDER BY ${orderBy}

SELECT COUNT(ITEM_NAME)
FROM ITEM

WHERE ITEM_NAME LIKE '%'||#{searchValue}||'%' 


WHERE 1 = 1


AND CATE_CODE = #{searchKeyword}


AND ITEM_PRICE >= #{minPrice}
AND ITEM_PRICE <= #{maxPrice}
AND ITEM_PRICE <= #{maxPrice}


AND ITEM_PRICE >= #{minPrice}


AND ITEM_PRICE <= #{maxPrice}


ORDER BY ${orderBy}

mapper.update:
