mapper.insert:
INSERT INTO MEMBER(
	ID
	, PW
	, NAME
	, TELL
	, POST_CODE
	, ROAD_ADDR
	, DETAIL_ADDR
	, GENDER
	, EMAIL
	, BIRTHDAY
) VALUES(
	#{id}
	, #{pw}
	, #{name}
	, #{tell}
	, #{postCode}
	, #{roadAddr}
	, #{detailAddr}
	, #{gender}
	, #{email}
	, #{birthday}
)

mapper.select:
SELECT ID
, NAME
, IS_ADMIN
FROM MEMBER
WHERE ID = #{id}
AND PW = #{pw}

SELECT ID
FROM MEMBER
WHERE ID = #{id}

mapper.delete:
DELETE FROM MEMBER
WHERE ID = #{id}
AND PW = #{pw}

mapper.select:
SELECT ID
, NAME
, EMAIL
FROM MEMBER
WHERE NAME = #{name}
AND EMAIL = #{email}

SELECT ID
, PW
, NAME
FROM MEMBER
WHERE ID = #{id}
AND NAME = #{name}

mapper.update:
UPDATE MEMBER 
SET PW = #{pw}
WHERE ID = #{id}

UPDATE MEMBER
SET POST_CODE = #{postCode}
	, ROAD_ADDR = #{roadAddr}
	, DETAIL_ADDR = #{detailAddr}
	, TELL = #{tell}
	, EMAIL = #{email}
	, PW = #{pw}
WHERE ID = #{id}
