mapper.insert:
INSERT INTO MEMBER(
	ID
	, PW
	, NAME
	, TELL
	, ADDR
	, GENDER
	, EMAIL
	, BIRTHDAY
) VALUES(
	#{id}
	, #{pw}
	, #{name}
	, #{tell}
	, #{addr}
	, #{gender}
	, #{email}
	, #{birthday}
)

mapper.select:
SELECT ID
, NAME
, IS_ADMIN
FROM MEMBER
WHERE ID = #{id}
AND PW = #{pw}

SELECT ID
FROM MEMBER
WHERE ID = #{id}

SELECT ID
, NAME
, EMAIL
FROM MEMBER
WHERE NAME = #{name}
AND EMAIL = #{email}

SELECT ID
, PW
, NAME
FROM MEMBER
WHERE ID = #{id}
AND NAME = #{name}
